      content: content,
      isComplete: false,
    };
    dispatch(addTodo(todo));
  };
  // if (todo.title.trim() === "" || todo.body.trim() === "") return;
  const onDeleteTodo = (id) => {
    const _todos = todoList.filter((p) => p.id !== id);
    dispatch(deleteTodo(id));
  };

  const onToggleTodo = (id) => {
    dispatch(toggleTodo(id));
  };

  return (
    <div className="page">
