                <div className="showingbox" key={todo.id}>
                  <Link to="/DetailPage:id">ÏÉÅÏÑ∏ÌéòÏù¥ÏßÄÎ°ú Ïàë! </Link>
                  <h2>{todo.title}</h2>
                  <div>{todo.content}</div>
                  <button
                    className="btns"
                    onClick={() => onDeleteTodo(todo.id)}
