import "./App.css";
import { useSelector } from "react-redux";
import { useState } from "react";
import { addTodo } from "./redux/modules/todos";

function App() {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [todoList, setTodoList] = useState([]);

  const addTodo = () => {
    const todo = {
      id: todoList.length,
      title: title,
      content: content,
      iscomplete: false,
    };
    setTodoList([...todoList, todo]);
  };
  return (
    <>
      <div>
        <div>
          <span>제목</span>
          <input onChange={(e) => setTitle.target.value} value={title}>
            console.log(setTitle.target.value)
          </input>
        </div>
        <div>
          <span>내용</span>
          <input
            onChange={(e) => setContent.target.value}
            value={content}
          ></input>
        </div>
        <button onClick={addTodo}>작성하기</button>
      </div>
      <div>
        <div>
          <h1>Working..</h1>
          {todoList.map((todo) => {
            return (
              <div key={todo.id} style={{ border: "1px solid black" }}>
                <h2>{todo.title}</h2>
                <div>{todo.content}</div>
                <button>삭제하기</button>
                <button>완료</button>
              </div>
            );
          })}
        </div>
        <div style={{ border: "1px solid black" }}>
          <h1>completed</h1>
          <div>
            <h2>리액트 공부하기</h2>
            <div>공부하기</div>
            <button>삭제하기</button>
            <button>취소하기</button>
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
